  state = {
    users : [
      'Aadharsh',
      'Nandy',
      'Raja',
      'Sundar'
    ]
  }


  userSelector = () => {
  return Math.floor(Math.random() * 4);
  }

  render() {
    let user = this.userSelector();
    return (
      <div className = {classes.App}>
        <HeaderArea username={this.state.users[user]}/>
        <TextInput/>
      </div>
    )

  }

}
