  render() {
    state = {
      users : [
        'Aadharsh',
        'Nandy',
        'Raja',
        'Sundar'
      ]
    }


    userIndex = null;
    userSelector = () => {
      userIndex = Math.floor(Math.random() * 4);
    }

    return (
      <div className = {classes.App}>
        <HeaderArea username={this.state.us}/>
        <TextInput/>
      </div>
    )

  }

}
export default App;
