import React, { Component } from 'react';
import classes from './App.module.css';

// Custom Components Declaration
import TextInput from '../components/MessageComponent/MessageComponent';
import HeaderArea from '../components/HeaderComponent/HeaderComponent';

class App extends Component {

  state = {
    users: [
      'Aadharsh',
      'Nandy',
      'Raja',
      'Sundar'
    ],
    messages: {
      usersent: []
    },

  }

  userSelector = () => {
    return Math.floor(Math.random() * 4);
  }

  sendBtnClick = () => {

    let msgstr = document.getElementById('msginput').value;
    const usercopy = [
      ...this.state.messages.usersent
    ];
    usercopy.push(msgstr);
    this.setState({messages: {usersent: usercopy}});
    console.log(usercopy);
    document.getElementById('msginput').value = "";
  }

   user = this.userSelector();
  render() {


    return (

      <div className={classes.App}>
        <HeaderArea username={this.state.users[this.user]} />
        <TextInput clicked={() => this.sendBtnClick()}/>
      </div>

    )

  }

}
export default App;
